import React, { Component } from "react";

export default class Contador extends Component {
  state = {
    numero: this.props.numeroInicial,
  };

  inc = () => {
    this.setState({
      numero: this.state.numero + 1
    });
  }

  render() {
    return (
      <div>
        <h2>Contador</h2>
        <button onClick={this.inc}></button>
      </div>
    );
  }
}
